pragma solidity ^0.4.2;

// Define Account contract
contract Account {

  // Map sender to organization addresses
  mapping (address => address[]) organizations;

  // Notify clients about an updated account
  event AccountUpdate(address sender, address organization);

  // Add account organizations
  function addAccountOrganization(address _organization) public returns (bool success) {
    // Add organization to organizations array
    organizations[msg.sender].push(_organization);
    // Emit account update event
    AccountUpdate(msg.sender, _organization);
    // Return success
    return true;
  }

  // Get account organizations
  function getAccountOrganizations() public constant returns (address[] organization) {
    // Return account organizations
    return organizations[msg.sender];
  }

}
