pragma solidity ^0.4.19;

contract Account {

  address private owner;

  mapping (address => string) accounts;

  event AccountUpdate(address sender, string account);

  function Account() public {
    owner = msg.sender;
  }

  function getAccount() public constant returns (string account) {
    return accounts[msg.sender];
  }

  function setAccount(string _account) public returns (bool success) {
    accounts[msg.sender] = _account;
    AccountUpdate(msg.sender, _account);
    return true;
  }

}
