pragma solidity ^0.4.2;

// Import Owned contract
import "./Owned.sol";

// Define Organization Contract
contract Organization is Owned {

  // Define organization owner
  address organizationOwner;

  // Define organization name
  string organizationName;

  // Define members list
  address[] membersList;

  // Notify clients about an updated organization
  event OrganizationUpdate(address sender, string name);

  // Define owner modifier
  modifier onlyOrganizationOwner {
    // Require ballot owner is sender
    require(organizationOwner == msg.sender);
    // Run function body
    _;
  }

  // Define contract constructor
  function Organization(address _owner) public {
    // Set organization owner
    organizationOwner = _owner;
  }

  // Get organization name and members
  function getOrganization() public constant returns (string name, address[] members) {
    // Return organization name and members
    return (organizationName, membersList);
  }

  // Update organization name
  function updateOrganizationName(string _name) public onlyOrganizationOwner returns (bool success) {
    // Update organization name
    organizationName = _name;
    // Emit orgaization update event
    OrganizationUpdate(msg.sender, _name);
    // Return success
    return true;
  }

}
