pragma solidity ^0.4.19;

contract Organization {

  address private owner;

  mapping (address => string) organizations;

  event OrganizationUpdate(address sender, string organization);

  function Organization() public {
    owner = msg.sender;
  }

  function getOrganization(address _organization) public constant returns (string organization) {
    return organizations[_organization];
  }

  function setOrganization(string _organization) public returns (bool success) {
    organizations[msg.sender] = _organization;
    OrganizationUpdate(msg.sender, _organization);
    return true;
  }

}
